const otherInfo = {
    text: "",
    nowTurn: 0,
    turnendText: "",
    logs: []
};

function otherInfoReducer(state = {}, action, gameStatus = {}) {
    switch (action.type) {
        case "TEXTUPDATE":
            return {
                text: action.text,
                nowTurn: state.nowTurn,
                turnendText: state.turnendText,
                logs: state.logs
            };
        case "FIRSTPLAYERSET":
            return {
                text: state.text,
                nowTurn: state.nowTurn,
                turnendText: action.name + "のターン",
                logs: state.logs
            }
        case "TURNPASSED":
            return {
                text: state.text,
                nowTurn: state.nowTurn + 1,
                turnendText: gameStatus.enemyName + "のターン",
                logs: state.logs
            };
        case "TURNRECEIVED":
            return {
                text: state.text,
                nowTurn: state.nowTurn + 1,
                turnendText: gameStatus.yourName + "のターン",
                logs: state.logs
            };
        case "LOGUPDATE":
            return {
                text: state.text,
                nowTurn: state.nowTurn,
                turnendText: state.turnendText,
                logs: [...state.logs, action.pushLog]
            };
        case "YOUWIN":
            return {
                text: state.text,
                nowTurn: state.nowTurn,
                turnendText: "あなたの勝ち！",
                logs: [...state.logs, "あなたの勝ち！"]
            };
        case "YOULOSE":
            return {
                text: state.text,
                nowTurn: state.nowTurn,
                turnendText: "対戦相手の勝ち…",
                logs: [...state.logs, "対戦相手の勝ち…"]
            };
        case "GAMEDESTROY":
            return {
                text: "",
                nowTurn: 0,
                turnendText: "",
                logs: []
            }
        default:
            return state;
    }
}

export default otherInfoReducer;
